import React, { Component } from "react";
import FormStep3 from '../classes/FormStep3';

export default class Step3 extends React.Component {

    state = {
        familyQuantities: [],
        desiredDailyValues: []
    }

    /**
     * Shows the table
     */
    renderTable() {
        return this.state.familyQuantities.map((famQuant, index) => {
            const { grains, veg, fruit, dairy, protein } = famQuant
            return (
                <tr key={grains}>
                    <td>{grains}</td>
                    <td>{veg}</td>
                    <td>{fruit}</td>
                    <td>{dairy}</td>
                    <td>{protein}</td>
                </tr>
            )
        })
    }

    /**
     * Shows the table header
     */
    renderTableHeader() {
        if (this.state.familyQuantities.length > 0) {
            let header = Object.keys(this.state.familyQuantities[0])
            //var count = 0; 
            //let newHeader = header.splice(0, 3); 
            return header.map((key, index) => {
                //if (count < 3) {
                return <th key={index}>{key.toUpperCase()}</th>
                //}
                //count++; 
            })
        }
    }

    render() {
        return (
            <div className="Step3">
                <FormStep3 />
                <div>
                    <h1 id='title'>You have...</h1>
                    <table id='familyQuantities'>
                        <tbody>
                            <tr>{this.renderTableHeader()}</tr>
                            {this.renderTable()}
                        </tbody>
                    </table>
                </div>
                <button onClick={e => this.continueToStep2(e)}>Continue to Step 2</button>
            </div>
        );
    }
}
