import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from "./Form"; 

function Person(category, age) {
  this.category = category; 
  this.age = age; 
  if (this.type.equals("Man")) {
    if (age >= 19 && age <= 30) {
      this.ageCategory = Ages._19to30_; 
      this.grain = 8; 
    } else if (age >= 31 && age <= 50) {
      this.ageCategory = Ages._31to50_; 
      this.grain = 7;
    } else if (age >= 51) {
      this.ageCategory = Ages._51nUp_; 
      this.grain = 6;
    } 
  } // Woman: 19-30 yrs, 31-50 yrs, 51+ yrs 
  else if (this.type.equals("Woman")) {
    if (age >= 19 && age <= 30) {
      this.ageCategory = Ages._19to30_; 
      this.grain = 6; 
    } else if (age >= 31 && age <= 50) {
      this.ageCategory = Ages._31to50_; 
      this.grain = 6;
    } else if (age >= 51) {
      this.ageCategory = Ages._51nUp_; 
      this.grain = 5;
    }
  } // Girl: 9-13 yrs, 14-18 yrs
  else if (this.type.equals("Girl")) {
    if (age >= 9 && age <= 13) {
      this.ageCategory = Ages._9to13_; 
      this.grain = 5; 
    } else if (age >= 14 && age <= 18) {
      this.ageCategory = Ages._14to18_; 
      this.grain = 6;
    } 
  } // Boy: 9-13 yrs, 14-18 yrs
  else if (this.type.equals("Boy")) {
    if (age >= 9 && age <= 13) {
      this.ageCategory = Ages._9to13_; 
      this.grain = 6; 
    } else if (age >= 14 && age <= 18) {
      this.ageCategory = Ages._14to18_; 
      this.grain = 8;
    } 
  } // Child: 2-3 yrs, 4-8 yrs
  else if (this.type.equals("Child")) {
    if (age >= 2 && age <= 3) {
      this.ageCategory = Ages._2to3_; 
      this.grain = 3; 
    } else if (age >= 4 && age <= 8) {
      this.ageCategory = Ages._4to8_; 
      this.grain = 5;
    } 
  }	
}

class App extends Component { 
  state = {
    fields: []
  }

  onSubmit = (fields) => {
    var entry = new Person(fields.category, fields.age); 
    var newFields = this.state.fields; 
    newFields.push(entry); 
    this.setState({ newFields })
  }; 

  resetFam = (e) => {
    this.setState({
      fields: []
    })
}; 

  render() {
    return (
      <div className="App">
        <Form onSubmit={fields => this.onSubmit(fields)}/>
        <button onClick={e => this.resetFam(e)}>Reset Family List</button>
        <p>{JSON.stringify(this.state.fields, null, 2)}</p>
      </div>
    );
  }
}

export default App;
