import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from "./Form"; 
import Person from "./Person"; 

class FamilySectionRow extends React.Component {
  render() {
    const category = this.props.category;
    return (
      <tr>
        <th colSpan="2">
          {category}
        </th>
      </tr>
    );
  }
}

class MemberRow extends React.Component {
  render() {
    const product = this.props.product;
    const name = product.stocked ?
      product.name :
      <span style={{color: 'red'}}>
        {product.name}
      </span>;

    return (
      <tr>
        <td>{name}</td>
        <td>{product.price}</td>
      </tr>
    );
  }
}

class ProductTable extends React.Component {
  render() {
    const filterText = this.props.filterText;
    const inStockOnly = this.props.inStockOnly;

    const rows = [];
    let lastCategory = null;

    this.props.products.forEach((product) => {
      if (product.name.indexOf(filterText) === -1) {
        return;
      }
      if (inStockOnly && !product.stocked) {
        return;
      }
      if (product.category !== lastCategory) {
        rows.push(
          <ProductCategoryRow
            category={product.category}
            key={product.category} />
        );
      }
      rows.push(
        <ProductRow
          product={product}
          key={product.name}
        />
      );
      lastCategory = product.category;
    });

    return (
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    );
  }
}

class App extends Component { 
  state = {
    fields: []
  }

  onSubmit = (fields) => {
    var entry = new Person(fields.category, fields.age); 
    var newFields = this.state.fields; 
    newFields.push(entry); 
    this.setState({ newFields })
  }; 

  resetFam = (e) => {
    this.setState({
      fields: []
    })
}; 

  render() {
    return (
      <div className="App">
        <Form onSubmit={fields => this.onSubmit(fields)}/>
        <button onClick={e => this.resetFam(e)}>Reset Family List</button>
        {/* <p>{JSON.stringify(this.state.fields, null, 4)}</p> ---> table here*/}

      </div>
    );
  }
}

export default App;
