import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from "./Form"; 
import Person from "./Person"; 

class FamilySectionRow extends React.Component {
  render() {
    const sections = this.props.sections;
    return (
      <tr>
        <th colSpan="3">
          {sections}
        </th>
      </tr>
    );
  }
}

class MemberRow extends React.Component {
  render() {
    return (
      <tr>
        <td>{this.props.firstName}</td>
        <td>{this.props.category}</td>
        <td>{this.props.age}</td>
      </tr>
    );
  }
}

class ProductTable extends React.Component {
  render() {
    const rows = [];
    let lastCategory = null;

    this.props.products.forEach((member) => {
      if (member.name !== lastCategory) {
        rows.push(
          <FamilySectionRow
            firstName={product.category}
            key={product.category} />
        );
      }
      rows.push(
        <MemberRow
          product={product}
          key={product.name}
        />
      );
      lastCategory = product.category;
    });

    return (
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    );
  }
}

class App extends Component { 
  state = {
    fields: []
  }

  onSubmit = (fields) => {
    var entry = new Person(fields.firstName, fields.category, fields.age); 
    var newFields = this.state.fields; 
    newFields.push(entry); 
    this.setState({ newFields })
  }; 

  resetFam = (e) => {
    this.setState({
      fields: []
    })
}; 

  render() {
    return (
      <div className="App">
        <Form onSubmit={fields => this.onSubmit(fields)}/>
        <button onClick={e => this.resetFam(e)}>reset</button>
        <p>{JSON.stringify(this.state.fields, null, 5)}</p> ---> table here

      </div>
    );
  }
}

export default App;
