import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from "./Form"; 
import Person from "./Person"; 

class FamilySectionRow extends React.Component {
  render() {
    const sections = this.props.sections;
    return (
      <tr>
        <th colSpan="3">
          {sections}
        </th>
      </tr>
    );
  }
}

class MemberRow extends React.Component {
  render() {
    const firstName = this.props.firstName; 
    const category = this.props.category; 
    const age = this.props.age; 
    return (
      <tr>
        <td>{firstName}</td>
        <td>{category}</td>
        <td>{age}</td>
      </tr>
    );
  }
}

class FamilyTable extends React.Component {
  render() {
    const rows = [];
    let nullName = null;

    this.props.members.forEach((member) => {
      if (member.name !== nullName) {
        rows.push(
          <FamilySectionRow
            sections={member.category}
            key={member.category} />
        );
      }
      rows.push(
        <MemberRow
          firstName={member.firstName}
          key={member.category}
        />
      );
      nullName = member.firstName;
    });

    return (
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Age</th>
            <th>This person is a...</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    );
  }
}

class App extends Component { 
  state = {
    familyMembers: []
  }

  onSubmit = (fields) => {
    var entry = new Person(fields.firstName, fields.category, fields.age); 
    var newFields = this.state.fields; 
    newFields.push(entry); 
    this.setState({ newFields })
  }; 

  resetFam = (e) => {
    this.setState({
      fields: []
    })
}; 

  render() {
    return (
      <div className="App">
        <Form onSubmit={fields => this.onSubmit(fields)}/>
        <button onClick={e => this.resetFam(e)}>reset</button>
        <p>{JSON.stringify(this.state.fields, null, 5)}</p> ---> table here
        <FamilyTable 
          members={this.props.members}
        />

      </div>
    );
  }
}

export default App;
