import React, { Component } from "react";
import FormStep2 from '../classes/FormStep2'; 
import history from '../classes/history'; 
import { grainQuantities, vegQuantities, fruitQuantities, dairyQuantities, proteinQuantities } from '../classes/foodLabels_Values'


function howLongWillItLast(grains, veg, fruit, dairy, protein, grainsCount, vegCount, fruitCount, dairyCount, proteinCount) {
    return [{
        daysOfGrains: +(grainsCount / grains), 
        daysOfVeg: +(vegCount / veg), 
        daysOfFruit: +(fruitCount / fruit), 
        daysOfDairy: +(dairyCount / dairy), 
        daysOfProtein: +(proteinCount, protein)
    }]; 
}

export default class Step2 extends React.Component {
    state = {
        foodList: [], 
        grainsCount: 0, 
        vegCount: 0, 
        fruitCount: 0, 
        dairyCount: 0, 
        proteinCount: 0
    }

    onSubmit = (fruitList) => {
        var entry = fruitList
        var newFields = this.state.fruitList; 
        newFields.push(entry); 
        console.log("quant: ", fruitList.quantity); 
        this.setState({ 
            grainsList: newFields, 
         })
         // now set label
        // if grain
        if (grainQuantities[fruitList.type]) {
            this.setState({
                grainsCount: (+this.state.grainsCount) + (+fruitList.quantity) * (+grainQuantities[fruitList.type])
            }); 
        } // if veg
        else if (vegQuantities[fruitList.type]) {
            this.setState({
                vegCount: (+this.state.vegCount) + (+fruitList.quantity) * (+grainQuantities[fruitList.type])
            });
        }// if fruit
        else if (fruitQuantities[fruitList.type]) {
            this.setState({
                fruitCount: (+this.state.grainsCount) + (+fruitList.quantity) * (+grainQuantities[fruitList.type])
            });

        }// if dairy
        else if (dairyQuantities[grainsList.type]) {
            this.setState({
                dairyCount: (+this.state.grainsCount) + (+fruitList.quantity) * (+grainQuantities[fruitList.type])
            });

        }// if protein
        else if (proteinQuantities[grainsList.type]) {
            this.setState({
                proteinCount: (+this.state.grainsCount) + (+fruitList.quantity) * (+grainQuantities[fruitList.type])
            });

        }






        console.log("this.state.grainsCount", +this.state.grainsCount); 

    }; 

    /**
   * Transitions to step 3
   */
  continueToStep3 = (e) => {
    const {famData} = this.props.location; 
    console.log('Family data: ' + famData);
    var willItLast = howLongWillItLast(famData[0].grains, 0, famData[0].fruit, 0, 0, this.state.grainsCount, 0, 0, 0, 0); 
    // go to next page
    history.push({
        pathname: ('/Step3'), 
        willItLast: willItLast
      });   
  }; 

    render() {
        return (
            <div className="Step2">
                <FormStep2 onSubmit={grainsList => this.onSubmit(grainsList)}/>
                <p>{JSON.stringify(this.state.grainsList, null, 2)}</p> ---> (grains list) table here
                <p>{JSON.stringify(this.state.grainsCount, null, 2)}</p> ---> (grains count) count here
                {/* <p>{JSON.stringify(this.props.state.familyMembers[0], null, 5)}</p> ---> table here */}
                <button onClick={e => this.continueToStep3(e)}>Calculate When I Will Run Out of Food!</button>
            </div>
        );
    }
}
