import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from "./Form"; 

function Person(category, age) {
  this.category = category; 
  this.age = age; 
}

class App extends Component { 
  state = {
    fields: []
  }

  onSubmit = (fields) => {
    var entry = new Person(fields.category, fields.age); 
    var newFields = this.state.fields; 
    newFields.push(entry); 
    this.setState({ newFields })
  }; 

  resetFam = (e) => {
    e.preventDefault(); 
    this.props.resetFam(this.state); 
    this.setState({
        category: "", 
        age: ""
    })
}; 

  render() {
    return (
      <div className="App">
        <Form onSubmit={fields => this.onSubmit(fields)}/>
        <button onClick={e => this.resetFam(e)}>Reset Family List</button>
        <p>{JSON.stringify(this.state.fields, null, 2)}</p>
      </div>
    );
  }
}

export default App;
