import React, { Component } from "react";
import FormStep3 from '../classes/FormStep3'; 

export default class Step3 extends React.Component {

    state = {
        familyQuantities: [], 
        desiredDailyValues: []
    }

   /**
    * Shows the table
    */
  renderTable() {
    return this.state.familyQuantities.map((famQuant, index) => {
      const { grains, veg, fruit, dairy, protein} = famQuant 
      return (
        <tr key={grains}>
          <td>{grains}</td>
          <td>{veg}</td>
          <td>{fruit}</td>
          <td>{dairy}</td>
          <td>{protein}</td>
        </tr>
      )
    })
  }

  /**
   * Shows the table header
   */
  renderTableHeader() {
    if (this.state.familyMembers.length > 0) {
      let header = Object.keys(this.state.familyMembers[0])
      var count = 0; 
      let newHeader = header.splice(0, 3); 
      return newHeader.map((key, index) => {
        if (count < 3) {
          return <th key={index}>{key.toUpperCase()}</th>
        }
        count++; 
      })
    }
  }

    render() {
        return (
            <div className="Step3">
                <FormStep3 />
                {/* <FormStep3 onSubmit={grainsList => this.onSubmit(grainsList)}/> */}
                {/* <p>{JSON.stringify(this.state.grainsList, null, 2)}</p> ---> table here
                <p>{JSON.stringify(this.state.grainsCount, null, 2)}</p> ---> count here } */}
                {/* <p>{JSON.stringify(this.props.state.familyMembers[0], null, 5)}</p> ---> table here */}
            </div>
        );
    }
}
